import React, { useState, useContext } from "react";
import { Link, useHistory } from "react-router-dom";
import "./login.css";
import InputLogin from "../common/inputLogin";
import { UserContext } from "../../App";


function Login(props) {
  const usercontext = useContext(UserContext);
  let history = useHistory();
  const [email, setemail] = useState("");
  const [password, setpassword] = useState("");
  const [errors, seterrors] = useState({})

  const handleChange = ({ currentTarget: input }) => {
    if (input.name === "email") {
      setemail(input.value);
    } else {
      setpassword(input.value);
    }
  };
  
  const validate =()=>{
      const fError ={}
      if (email.trim() === "") {
          fError.email = "user email required"
      } 
      if(password.trim() === ""){
        fError.password = "user password required"
      }
      return (fError)
  }
  const submitHandler = (e) => {
    console.log("submit");
    e.preventDefault();
    let error = validate();
    seterrors({error : error })
    if (error) return;
    props.Login(email, password);
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="form-container">
        <h2>Login</h2>
        <InputLogin
          onChange={handleChange}
          name="email"
          class_main_div="input-email"
          value={email}
        />
        <InputLogin
          font_awesome_class="fas fa-user-lock"
          onChange={handleChange}
          name="password"
          class_main_div="input-password"
          value={password}
        />

        <h3>
          <span style={{ color: "red" }} id="success"></span>
        </h3>

        <button type="submit">Login</button>
        <center>or</center>

        <Link to="/register" style={{ color: "black" }}>
          REGISTER
        </Link>
      </div>
    </form>
  );
}

export default Login;
