import React,{useContext,useState} from 'react'
import {Link} from 'react-router-dom'
import {UserContext} from '../App'
function Nav(props) {
  const user = useContext(UserContext)
  const [_id, set_id] = useState('')

  const userId =() =>{
    let id = localStorage.getItem('auth_token').split(' ')
    return _id ? id[length-1] : 'nothing'
  }

  const logout =async() => {
      await props.setuser({})
      localStorage.clear()
  }

  React.useEffect(()=>{
    let id = localStorage.getItem("auth_token")
    console.log('idddddd' ,id , '_id : ' ,_id)
    if (id) {
      set_id(id)
      return true
    } else {set_id('')
      return false
    } 
  },[user])
    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container-fluid">
            <Link className="navbar-brand" to="/">site name</Link>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarSupportedContent">
         <ul className="navbar-nav me-auto mb-2 mb-lg-0">
          { !_id && <li className="nav-item">
             <Link className="nav-link active" aria-current="page" to="/login">login</Link>
           </li>}
           { _id && <li className="nav-item right">
             <Link className="nav-link active" aria-current="page" to="/login">log out</Link>
           </li>}
          { !_id && <li className="nav-item">
             <Link className="nav-link active" aria-current="page"  to="/register">register</Link>
           </li>}

           {_id && <Link className="nav-link active" aria-current="page" to={`/profile:${userId()}`} >profile</Link> }
 
         </ul>
        
         </div>
        </div>
      </nav>
        
    )
}

export default Nav
