import React, { Component } from 'react'

export default class Form extends Component {
 
    constructor(props) {
        super(props)
    
        this.state = {
          errors : {},
          data : {}
        }
    }

    handleChange = ({ currentTarget: input }) => {
        const ferrors = { ...errors};
        const errorMessage = validateProperty(input);
        if (errorMessage) ferrors[input.name] = errorMessage;
        else delete ferrors[input.name];
    
        if (input.name === "email") {
         setstate(input.value);
          seterrors(ferrors)
        } else {
          setpassword(input.value);
          seterrors(ferrors)
        }
      };

    validateProperty = ({ name, value }) => {
        if (name === "email") {
            // condition when change here
          if (value.trim() === "") return "user email error something in change";
        }
        if(name ==="password") {
            // condition when change here
            if (value.trim() === '' ) return "user email error something in change"
        }
    };

    validate = () => {
        const fError = {};
        if (email.trim() === "") {
          fError.email = "user email required";
        }
        if (password.trim() === "") {
          fError.password = "user password required";
        }
        return Object.keys(fError) === 0 ? null : fError;
      };

      submitHandler = (e) => {
        console.log("submit");
        e.preventDefault();
        const errors = validate();
    
        seterrors(errors || {});
    
        if (errors) return;
        props.Login(email, password);
      };

      submitHandler = (e) => {
        console.log("submit");
        e.preventDefault();
        const errors = validate();
    
        seterrors(errors || {});
    
        if (errors) return;
        this.doSubmit()
      };

      submitHandler = (e) => {
        console.log("submit");
        e.preventDefault();
        const errors = validate();
    
        seterrors(errors || {});
    
        if (errors) return;
        doSubmit()
      };
}
