import React,{useState} from 'react'
import { Link,hi } from 'react-router-dom'
import './login.css'
import axios from 'axios'
import {login} from '../shared/auth'
const endpoint = "http://localhost:5000";

const headers = {
  "Content-Type": "Application/json",
};
function Login() {
    const [email, setemail] = useState('')
    const [password, setpassword] = useState('')

    const handleEmail =(e) =>{
        setemail(e.target.value)
    }
    const handlePassword =(e) =>{
        setpassword(e.target.value)
    }

    const submitHandler = (e) =>{
        console.log('submit');
        e.preventDefault();
        axios
      .post(
        `${endpoint}/signin`,
        { email: email, password: password },
        { headers: headers }
      )
      .then((user) => {
        localStorage.setItem("auth_token",`barer ${user.data.token}`)
          console.log(user);
          return user;
      })
      .catch((error) => {
        alert(error);
      });

    }

    return (
        <form onSubmit={submitHandler} >
        <div className="form-container">
        <h2>Login</h2>
            
        <div className="input-email">
            <label > <i className="far fa-envelope"></i> Email</label>
            <input onChange={handleEmail} name="email" required type="email" value={email} placeholder="email address" />
           
        </div>

        <div className="input-password">
            <label > <i className="fas fa-user-lock"></i> Password</label>
            <input onChange={handlePassword}  required type="password" value={password}  placeholder="Password"  />
        </div>
		
		<h3>
			<span style={{color: 'red'}} id="success"></span>

		</h3>		
		
		<button   type="submit">Login</button>
		<center>or</center>
		
			<Link to="/register" style={{color : 'black'}} >REGISTER</Link>
		
		
        </div>
        </form>
    )
}

export default Login
