import React, { useState } from "react";
import InputLogin from "../common/InputForm";
import { Link } from "react-router-dom";
import joi from 'joi-browser'
const initialStateForm = {
    data :  {username: "", password: "", email: "", phone: ""},
    errors : {}
 };

function Register() {
  const [form, setform] = useState(initialStateForm);

  const schema = {
      username : joi.string().required().min(4).max(15).label('Username'),
      password  : joi.string().required().min(8).max(30).label('Password'),
      email : joi.string().required().email().label('Email'),
      phone : joi.string().regex(/07(7|8|9)\d{7}/).label('Phone')
  }

  const validate = () =>{
      const result = joi.validate(form.data, schema,{abortEarly : false})
      console.log(result);
      if (!result.error) return null;
      const errors = {}
      for (let err of result.error.details) {
          errors[err.path[0]] = err.message;
      }
      return errors
  }



  const submitHandler = (event) => {
    event.preventDefault();
    const errors =  validate()
    setform({ ...form, errors :errors || {} })
    if (errors) return;

    alert("call server")

  };

  React.useEffect(()=>{
       console.log(form);
  }, [form] )

  const validateProperty = ({name,value}) =>{
      const obj ={[name]:value}
      const {error} = joi.validate(obj,schema[name])
     return error
       
  }
  const handleChange = ({ currentTarget: input }) => {
      const data = { ...form.data }
      const errors = { ...form.errors}
      const errorsV= validateProperty(input)
      console.log(errorsV);
      if (errorsV) {
          errors[input.name] = errorsV
          setform({ ...form,errors})
      }
      
      data[input.name] = input.value;
      setform({ ...form, data })
      
  };

  return (
    <div>
     
      <form onSubmit={submitHandler}>
        <div className="form-container">
          <h2>Register</h2>

          <InputLogin
            onChange={handleChange}
            font_awesome_class="fas fa-user"
            name="username"
            value={form.data.username}
            error={form.errors.username}
          />
          <InputLogin
            onChange={handleChange}
            font_awesome_class=""
            name="email"
            value={form.data.email}
            error={form.errors.email}
          />
          <InputLogin
            onChange={handleChange}
            font_awesome_class="fas fa-user-lock"
            class_main_div="input-phone"
            name="password"
            value={form.data.password}
            error={form.errors.password}
          />
          <InputLogin
            onChange={handleChange}
            font_awesome_class=""
            name="phone"
            value={form.data.phone}
            error={form.errors.phone}
          />
          <button type="submit">Register</button>
          <center>or</center>
          <Link to="/login" style={{ color: "black" }}>
            Login
          </Link>
        </div>
      </form>
     
    </div>
  );
}

export default Register;
