import React, { Component } from "react";
import Form from "../common/Form";
import InputLogin from "../common/InputForm";

class Register extends Form {
  constructor(props) {
    super(props);

    this.state = {
      data: { username: "", password: "", phone: 0, email: "" },
      errors: {},
    };
  }

  doSubmit = () => {
    this.props.Register(this.state.data);
  };

  render() {
    const { username, email, phone, password } = this.state.data;
    return (
      <form onSubmit={this.submitHandler}>
        <div className="form-container">
          <h2>Register</h2>

          <InputLogin  name='username' value={username} />
          <InputLogin name='email' value={email} />
          <InputLogin class_main_div="input-phone" name='password' value={password} />
          <InputLogin name='phone' value={phone} />
        </div>
      </form>
    );
  }
}
export default Register;
