import React,{useContext} from 'react'
import {Link} from 'react-router-dom'
import {UserContext} from '../App'
function Nav() {
  const user = useContext(UserContext)
  const [_id, set_id] = useState('')

  const userId =() =>{
    return is_LoggedIn ? localStorage.getItem('auth_token') : 'nothing'
  }
  const is_LoggedIn = ()=>{
    let id = localStorage.getItem("auth_token")
    if (id) {
      set_id()
      return true
    } else {
      return false
    } 
  }
    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container-fluid">
            <Link className="navbar-brand" to="/">site name</Link>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarSupportedContent">
         <ul className="navbar-nav me-auto mb-2 mb-lg-0">
          { !is_LoggedIn && <li className="nav-item">
             <Link className="nav-link active" aria-current="page" to="/login">login</Link>
           </li>}
          { !is_LoggedIn && <li className="nav-item">
             <Link className="nav-link active" aria-current="page"  to="/register">register</Link>
           </li>}

           {is_LoggedIn && <Link className="nav-link active" aria-current="page" to={`/profile:${userId()}`} >profile</Link> }
 
         </ul>
        
         </div>
        </div>
      </nav>
        
    )
}

export default Nav
